import React from 'react';
import './pokedex.css';
import pokemon from './pokemon.jsx';
import { useState } from 'react';


const Pokedex = () => {
    const [selectPokemon, selectedPokemon] = useState(null);
    const handleButtonClick = (pokemon) => {
      selectedPokemon(pokemon);
    };
    return (
        <div>
            <div className="header">Pokédex</div>
            <div className="bar"></div>
            <div className="button_section">
            <button className={`pokemon-button ${selectedPokemon === 'Pikachu' ? 'active' : ''}`} 
            onClick={() => handleButtonClick('Pikachu')}>
            Pikachu
            </button>                
            <button className="pokemon_buttons">Charizard</button>
            <button className="pokemon_buttons">Geodude</button>
            <button className="pokemon_buttons">Rattata</button>
            </div>
            <div className="select">Select a Pokémon!</div>

        </div>
    );
}

export default Pokedex;
