import React, { useState } from 'react';
import { pokemon } from './pokemon.jsx';
import './pokedex.css';

const Pokedex = () => {
    const [selectedPokemon, setSelectedPokemon] = useState(null);

    return (
        <div>
            <div className="header">Pokedex</div>
            <div className="button-container">
                {['Pikachu', 'Charizard', 'Geodude', 'Rattata'].map(name => (
                    <button className="button" onClick={() => setSelectedPokemon(name)}>{name}</button>
                ))}
            </div>
            {selectedPokemon ? (
                <Card name={selectedPokemon} data={pokemon[selectedPokemon]} />
            ) : (
                <div style={{ textAlign: 'center' }}>Choose a Pokemon</div>
            )}
        </div>
    );
}
